(defn process-line
  [line]
  (let [[d & n] line]
    [d (Integer/parseInt (apply str n))]))

(defn process-input
  [input]
  (let [lines (clojure.string/split-lines input)]
    (map process-line lines)))

(def processed-input (process-input input))

(def MAX-DIAL 100) ;; 0 to 99 inclusive!

(defn change-dial
  [dial [dir amount :as move]]
  (case dir
    \R (mod (+ dial amount) MAX-DIAL)
    \L (mod (- dial amount) MAX-DIAL)))

(defn solve
  [processed-input]
  (->> (reductions change-dial 50 processed-input)
       (filter zero?)
       count))

; 3
(solve (process-input "L68\nL30\nR48\nL5\nR60\nL55\nL1\nL99\nR14\nL82"))

; 1118
(solve processed-input)

; part 2

(defn expanded-input
  [[dir amount :as move]]
  (repeat amount [dir 1]))

(defn expanded-processed-input
  [processed-input]
  (vec (mapcat expanded-input processed-input)))

; 6
(solve (expanded-processed-input (process-input "L68\nL30\nR48\nL5\nR60\nL55\nL1\nL99\nR14\nL82")))

; 6289
(solve (expanded-processed-input processed-input))
