(def THRESHOLD 4)

(defn cols
  [input]
  (count (first input)))

(comment
 (= (cols input) 139)
 )

(defn rows
  [input]
  (count input))

(comment
  (= (rows input) 139)
  )

(defn surrounds
  [[r c]]
  [
   [(dec r) (dec c)]
   [(dec r) c]
   [(dec r) (inc c)]

   [r (dec c)]
   ;[r c]
   [r (inc c)]

   [(inc r) (dec c)]
   [(inc r) c]
   [(inc r) (inc c)]])

(defn surrounding
  [input pos]
  (->> (surrounds pos)
       (map (partial get-in input))))

(defn filter-surrounding
  [input pos set-e]
  (filter set-e (surrounding input pos)))

(defn removable?
  [input pos]
  (and
    (= (get-in input pos) \@)
    (< (count (filter-surrounding input pos #{\@})) THRESHOLD)))

(defn all-pos
  [input]
  (for [r (range (rows input))
        c (range (cols input))]
    [r c]))

(defn solve
  [input]
  (->> (all-pos input)
       (map (partial removable? input))
       (filter true?)
       count))

(solve test-input)

(solve input)

; part 2

(def test-input2 (mapv (comp vec seq) test-input))

(def input2 (mapv (comp vec seq) input))

(defn move
  [input]
  (->> (all-pos input)
       (keep (fn [pos]
              (when (removable? input pos)
                pos)))
       (reduce (fn [a [r c :as _e]] (assoc-in a [r c] \.)) input)))

(defn until-repeat
  [[f s :as sq]]
  (if (or (= f s)
          (nil? s))
    (list f)
    (cons f (until-repeat (rest sq)))))

(defn count-rolls
  [input]
  (count (filter #(= % \@) (apply concat input))))

(defn solve2
  [input]
  (- (count-rolls input)
     (count-rolls (last (until-repeat (iterate move input))))))

; 43
(solve2 test-input2)

; 8366
(solve2 input2)

